import React, { useEffect, useState } from "react";
import { Form } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { useLocation, useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { ResetPassword } from "../../../features/apiCall";
import AuthLayout from "../AuthLayout";
const ErrorToastOptions = {
  position: "bottom-center",
  autoClose: 3000,
  pauseOnHover: true,
  draggable: true,
  theme: "dark",
};
const UpdatePassword = () => {
  const location = useLocation();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const email = location.state;
  useEffect(() => {
    if (!email) {
      navigate("/forgot-password");
    }
  }, [email]);

  const [values, setValues] = useState({
    newPassword: "",
    confirmPassword: "",
  });
  const handlePwdChange = (e) => {
    setValues({ ...values, [e.target.name]: e.target.value });
  };

  const handleValidation = () => {
    const { newPassword, confirmPassword } = values;

    if (newPassword.length < 8) {
      toast.error("Password must contain 8 characters.", ErrorToastOptions);
      return false;
    } else if (confirmPassword !== confirmPassword) {
      toast.error("Passwords do not match.", ErrorToastOptions);
      return false;
    }

    return true;
  };

  const handlePwdSubmit = async (e) => {
    e.preventDefault();

    const { newPassword, confirmPassword } = values;

    if (handleValidation()) {
      if (
        await ResetPassword(dispatch, { email, newPassword, confirmPassword })
      ) {
        navigate("/login");
        setValues({
          newPassword: "",
          confirmPassword: "",
        });
      }
    }
  };
  return (
    <AuthLayout>
      <section className="forgot-password">
        <h3 className="mb-4 font-weight-bold">Change Password</h3>
        <p className="mb-1 mt-4 email ml-1">
          Type your new password to continue
        </p>
        <Form onSubmit={handlePwdSubmit}>
          <Form.Control
            type="password"
            onChange={handlePwdChange}
            name="newPassword"
            required
            value={values?.newPassword}
            className="mb-3"
          />
          <Form.Control
            className="mb-3"
            type="password"
            onChange={handlePwdChange}
            name="confirmPassword"
            required
            value={values?.confirmPassword}
          />

          <button type="submit" className="purple-button w-100">
            Change Password{" "}
          </button>
        </Form>
      </section>
      <section className="illustration-container">
        <img
          src="images/UpdatePassword.png"
          className="illustration"
          style={{ width: "auto", height: "200px" }}
        />
      </section>
      <BootstrapModal
        showModal={showModal}
        handleClose={handleClose}
        modalTitle={""}
        modalContent={<ModalContent />}
        className="check_your_modal_container"
      />
    </AuthLayout>
  );
};

export default UpdatePassword;
const ModalContent = ({ email }) => {
  const [otp, setOtp] = useState(["", "", "", "", "", ""]);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const handleOtpChange = (index, value) => {
    const newOtp = [...otp];
    newOtp[index] = value;
    setOtp(newOtp);

    if (value && index < 5) {
      document.getElementById(`otp-input-${index + 1}`).focus();
    }
    if (newOtp.every((code) => code !== "")) {
      sendOtpRequest(newOtp.join(""));
    }
  };
  const sendOtpRequest = async (otpValue) => {
    if (email) {
      try {
        if (await VerifyOtp(dispatch, { email: email, code: otpValue })) {
          navigate("/update-password", { state: email });
        }
      } catch (error) {
        // Handle any errors that might occur during the OTP verification
        console.error("Error during OTP verification:", error);
      }
    }
  };
  return (
    <section className="check-your-inbox">
      <i className="fa fa-envelope" aria-hidden="true" />
      <div className="d-flex check-your-box-texts">
        <h5>Check Your Inbox</h5>
        <p>
          Please open the link in the email to continue <br />
          or Enter the verification code we sent to{" "}
        </p>
        <h6>{email}</h6>
      </div>

      {/* <Form.Control /> */}
      <div className="otp-input-container">
        {otp.map((digit, index) => (
          <input
            key={index}
            id={`otp-input-${index}`}
            type="text"
            maxLength="1"
            value={digit}
            onChange={(e) => handleOtpChange(index, e.target.value)}
            className="otp-input m-3"
          />
        ))}
      </div>
      <p>Resend (60s)</p>
      <p style={{ fontSize: "12px" }}>Need Help ?</p>
      <button
        type="submit"
        className="purple-button w-50"
        style={{ zIndex: "-2" }}
      >
        Update Password{" "}
      </button>
    </section>
  );
};
