import React, { useState } from "react";
import { NavLink, Row, ToastContainer } from "react-bootstrap";

const DoctorMenu = ({ children }) => {
  const [selectedItem, setSelectedItem] = useState(null);

  const handleItemClick = (itemName) => {
    setSelectedItem(itemName);
  };

  const navigationMenu = [
    { itemName: "Home", iconPath: "/images/icons/home.svg" },
    { itemName: "Recent Bookings", iconPath: "/images/icons/calender.svg" },
    { itemName: "Recent Evaluation", iconPath: "/images/icons/list-check.svg" },
    {
      itemName: "Recent Prescription",
      iconPath: "/images/icons/file-lines.svg",
    },
    { itemName: "Profile", iconPath: "/images/icons/user.svg" },
  ];

  return (
    <>
      <div>
        <Row className="d-flex">
          <div className="doctor-menu-cont">
            <main className="w-100 h-100 doctor-menu text-center text-secondary">
              <img
                src="images/DoctorMenuLogo.png
              "
                width={130}
                className="menu-logo"
              />
              <ul className="list-unstyled text-start ">
                {navigationMenu.map((item, index) => (
                  <NavigationItem
                    key={index}
                    itemName={item.itemName}
                    iconPath={item.iconPath}
                    handleItemClick={() => handleItemClick(item.itemName)}
                    isSelected={selectedItem === item.itemName}
                  />
                ))}
              </ul>
              <NavLink
                className="text-left position-absolute  "
                style={{ bottom: "30px" }}
              >
                <div>
                  <i className="fa fa-sign-out" aria-hidden="true" />
                  <p className="menu-list-items">Logout </p>
                </div>
              </NavLink>
            </main>
          </div>
          <div
            className="children-cont"
            style={{
              backgroundColor: "#F2F8FD",
              padding: "0px",
            }}
            // style={{ backgroundColor: "#F2F8FD", padding: "0px" }}
          >
            <div className="vh-100 p-0">
              <main>{children}</main>
              <ToastContainer position="top-center" />
            </div>
          </div>
        </Row>
      </div>
    </>
  );
};

export default DoctorMenu;
const NavigationItem = ({
  itemName,
  iconPath,
  handleItemClick,
  isSelected,
}) => {
  return (
    <li className={isSelected ? "selected" : ""}>
      <NavLink to="#" onClick={handleItemClick}>
        <div className="menu-icon-cont">
          <img src={iconPath} alt={itemName} className="icons" />
        </div>
        <span className="menu-list-items">{itemName}</span>
      </NavLink>
    </li>
  );
};
