import React, { useState } from "react";
import { Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import BootstrapModal from "./Components/BootstrapModal";

const DoctorSelectUser = () => {
  const [selectedUserType, setSelectedUserType] = useState("");
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();
  const handleSubmit = () => {
    alert(selectedUserType);
    if (selectedUserType === "New") {
    } else if (selectedUserType === "Existing") {
      console.log("modal opened for verification");
      navigate("/doctor/dashboard/doctor-service-selection");
    }
  };
  const handleUserTypeChange = (userType) => {
    setSelectedUserType(userType);
  };
  // modal close
  const handleClose = () => setShowModal(false);
  return (
    <>
      <section
        className="text-center d-flex flex-column justify-content-center align-items-center "
        style={{ gap: "24px", width: "40%" }}
      >
        <img src="/images/areseliteLogo.png" width={100} alt="logo" />
        <h5>Please select Type of user</h5>

        <div
          className="d-flex flex-wrap justify-content-center "
          style={{ gap: "24px" }}
        >
          <button
            className={`doctor-user-type ${
              selectedUserType === "New" ? "selected" : ""
            }`}
            onClick={() => handleUserTypeChange("New")}
          >
            New
          </button>
          <button
            className={`doctor-user-type ${
              selectedUserType === "Existing" ? "selected" : ""
            }`}
            onClick={() => handleUserTypeChange("Existing")}
          >
            Existing
          </button>
        </div>
        <Button
          onClick={handleSubmit}
          className="purple-button "
          style={{ width: "332px", height: "62px" }}
        >
          Continue
        </Button>
      </section>
      <BootstrapModal
        showModal={showModal}
        handleClose={handleClose}
        modalTitle={"Please enter email to verify"}
        modalContent={<ModalContent />}
        className="check_your_modal_container"
      />
    </>
  );
};

export default DoctorSelectUser;
const ModalContent = () => {
  return (
    <section className="check-your-inbox">
      <i className="fa fa-envelope" aria-hidden="true" />
      <div className="d-flex check-your-box-texts">
        <h5>Check Your Inbox</h5>
        <p>
          Please open the link in the email to continue <br />
          or Enter the verification code we sent to{" "}
        </p>
        <h6>{email}</h6>
      </div>

      {/* <Form.Control /> */}
      <div className="otp-input-container">
        {otp.map((digit, index) => (
          <input
            key={index}
            id={`otp-input-${index}`}
            type="text"
            maxLength="1"
            value={digit}
            onChange={(e) => handleOtpChange(index, e.target.value)}
            className="otp-input m-3"
          />
        ))}
      </div>
      <p>Resend (60s)</p>
      <p style={{ fontSize: "12px" }}>Need Help ?</p>
      <button
        type="submit"
        className="purple-button w-50"
        style={{ zIndex: "-2" }}
      >
        Update Password{" "}
      </button>
    </section>
  );
};
