import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { VerifyAthelete } from "../../features/apiCall";
import "../../styles/modal.css";
import BootstrapModal from "./Components/BootstrapModal";
const DoctorSelectUser = () => {
  const [selectedUserType, setSelectedUserType] = useState("");
  const [showModal, setShowModal] = useState(false);
  const navigate = useNavigate();
  const handleSubmit = () => {
    if (selectedUserType === "New") {
      navigate("/doctor/dashboard/doctor-service-selection");
    } else if (selectedUserType === "Existing") {
      console.log("modal opened for verification");
      setShowModal(true);
      // navigate("/doctor/dashboard/doctor-service-selection");
    }
  };
  const handleUserTypeChange = (userType) => {
    setSelectedUserType(userType);
  };
  // modal close
  const handleClose = () => setShowModal(false);
  return (
    <>
      <section
        className="text-center d-flex flex-column justify-content-center align-items-center "
        style={{ gap: "24px", width: "40%" }}
      >
        <img src="/images/areseliteLogo.png" width={100} alt="logo" />
        <h5>Please select Type of user</h5>

        <div
          className="d-flex flex-wrap justify-content-center "
          style={{ gap: "24px" }}
        >
          <button
            className={`doctor-user-type ${
              selectedUserType === "New" ? "selected" : ""
            }`}
            onClick={() => handleUserTypeChange("New")}
          >
            New
          </button>
          <button
            className={`doctor-user-type ${
              selectedUserType === "Existing" ? "selected" : ""
            }`}
            onClick={() => handleUserTypeChange("Existing")}
          >
            Existing
          </button>
        </div>
        <Button
          onClick={handleSubmit}
          className="purple-button "
          style={{ width: "332px", height: "62px" }}
        >
          Continue
        </Button>
      </section>
      <BootstrapModal
        showModal={showModal}
        handleClose={handleClose}
        modalTitle={"Please enter email to verify"}
        modalContent={<ModalContent handleClose={handleClose} />}
        className="verification-athelete"
      />
    </>
  );
};

export default DoctorSelectUser;
const ModalContent = ({ handleClose }) => {
  const [email, setEmail] = useState("");
  const dispatch = useDispatch();
  // const { isFetching } = useSelector((state) => state.auth);

  const handleVerification = async () => {
    if (await VerifyAthelete(dispatch, { email })) {
      handleClose;
    }
  };
  return (
    <section className="">
      <Form onSubmit={handleVerification}>
        <Form.Control
          type="email"
          name="email"
          placeholder="Enter Existing Email ID"
          onChange={(e) => setEmail(e.target.value)}
          value={email}
          className="mb-3 "
        />
      </Form>
      <button
        onClick={handleClose}
        className={"bt-2  w-25 "}
        // style={{ zIndex: "-2" }}
      >
        Close
      </button>
      <button
        type="submit"
        className={"bt-2  w-25 bt-3 "}
        // style={{ zIndex: "-2" }}
      >
        Verify
      </button>
    </section>
  );
};
