import { toast } from "react-toastify";
import axios from "../utils/axios.js";

import { parseError } from "../utils/parseError.js";
import {
  Failure,
  Start,
  Success,
  loginFailure,
  loginStart,
  loginSuccess,
} from "./authSlice.js";

const ErrorToastOptions = {
  position: "bottom-center",
  autoClose: 3000,
  pauseOnHover: true,
  draggable: true,
  // theme: "dark",
};
const successToastOptions = {
  position: "top-center",
  autoClose: 3000,
  pauseOnHover: true,
  draggable: true,
  theme: "light",
};
export const login = async (dispatch, user) => {
  dispatch(loginStart());

  const { email, password } = user;

  try {
    const { data } = await axios.post("/api/doctor/login", { email, password });
    console.log(data);
    toast.success("Logged in Sucessfully!", successToastOptions);
    dispatch(loginSuccess(data));
  } catch (error) {
    const errorMessage = parseError(error);
    toast.error(errorMessage, ErrorToastOptions);
    dispatch(loginFailure(errorMessage));
  }
};
export const SendOtp = async (dispatch, email) => {
  dispatch(Start());

  try {
    const { data } = await axios.post("/api/doctor/send-forgot-password-code", {
      email,
    });
    console.log(data);
    toast.error("OTP Sent!", SucessToastOptions);
    dispatch(Success(data));
    return true;
  } catch (error) {
    console.log(error);
    const errorMessage = parseError(error);
    toast.error(errorMessage, ErrorToastOptions);
    dispatch(Failure(errorMessage));
    return false;
  }
};
