import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";
import DoctorMenu from "../components/layout/DoctorMenu";
import DoctorTodayAppointment from "../components/layout/DoctorTodayAppointment";
// Import your DoctorTodayAppointment component

const DoctorAppointment = () => {
  const [formData, setFormData] = useState({
    email: "",
    date: "",
    time: "",
    selectedDoctor: "", // Added for the dropdown
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Add any logic needed when the form is submitted
    console.log("Form Data:", formData);
    // Add additional logic or state updates as needed
  };

  return (
    <DoctorMenu>
      <div className="d-flex Doctor-Consul  ">
        <section className="w-40 p-5">
          <Form onSubmit={handleSubmit}>
            <Form.Group controlId="formDate" className="mb-3">
              <Form.Label>Appointment Date</Form.Label>
              <Form.Control
                type="date"
                name="date"
                value={formData.date}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formTime" className="mb-3">
              <Form.Label>Appointment Time</Form.Label>
              <Form.Control
                type="time"
                name="time"
                value={formData.time}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Group controlId="formDoctor" className="mb-3">
              <Form.Label>Select Trainer/Doctor</Form.Label>
              <Form.Control
                as="select"
                name="selectedDoctor"
                value={formData.selectedDoctor}
                onChange={handleChange}
              >
                <option value="">Select a Trainer/Doctor</option>
                <option value="Dr. LaPlaca">Dr. LaPlaca</option>
                {/* Add more options as needed */}
              </Form.Control>
            </Form.Group>

            <Button
              className="purple-button mt-5 m-auto"
              type="submit"
              style={{ width: "332px", height: "62px" }}
            >
              Next
            </Button>
          </Form>
        </section>
        <DoctorTodayAppointment />
      </div>
    </DoctorMenu>
  );
};

export default DoctorAppointment;
