import { toast } from "react-toastify";
import axios from "../utils/axios.js";
import { parseError } from "../utils/parseError.js";
import {
  Failure,
  loginFailure,
  loginStart,
  loginSuccess,
  Start,
  Success,
} from "./authSlice.js";

const ErrorToastOptions = {
  position: "bottom-center",
  autoClose: 3000,
  pauseOnHover: true,
  draggable: true,
  theme: "dark",
};
const successToastOptions = {
  position: "top-center",
  autoClose: 3000,
  pauseOnHover: true,
  draggable: true,
  theme: "light",
};
export const login = async (dispatch, user) => {
  dispatch(loginStart());

  const { email, password } = user;

  try {
    const { data } = await axios.post("/api/doctor/login", { email, password });
    // console.log(data);
    toast.success("Logged in Sucessfully!", successToastOptions);
    dispatch(loginSuccess(data));
  } catch (error) {
    const errorMessage = parseError(error);
    toast.error(errorMessage, ErrorToastOptions);
    dispatch(loginFailure(errorMessage));
  }
};
export const SendOtp = async (dispatch, email) => {
  dispatch(Start());
  // const { email } = email;

  try {
    const { data } = await axios.post(
      "/api/doctor/send-forgot-password-code",
      email
    );
    // console.log(data);
    toast.success("OTP Sent!", successToastOptions);
    dispatch(Success(data));
    return true;
  } catch (error) {
    console.log(error);
    const errorMessage = parseError(error);
    toast.error(errorMessage, ErrorToastOptions);
    dispatch(Failure(errorMessage));
    return false;
  }
};
export const VerifyOtp = async (dispatch, { email, code }) => {
  dispatch(Start());

  try {
    const { data } = await axios.post("/api/doctor/validate-code", {
      email,
      code,
    });
    // console.log(data);
    toast.success("Verified!", successToastOptions);
    dispatch(Success(data));
    return true;
  } catch (error) {
    // console.log(error);
    const errorMessage = parseError(error);
    toast.error(errorMessage, ErrorToastOptions);
    dispatch(Failure(errorMessage));
    return false;
  }
};
export const ResetPassword = async (
  dispatch,
  { email, newPassword, confirmPassword }
) => {
  dispatch(Start());
  try {
    const { data } = await axios.post("/api/doctor/reset-password", {
      email,
      new_password: newPassword,
      confirm_password: confirmPassword,
    });
    toast.success("Password changed successfully", successToastOptions);
    dispatch(Success(data));
    return true;
  } catch (error) {
    // console.log(error);
    const errorMessage = parseError(error);
    toast.error(errorMessage, ErrorToastOptions);
    dispatch(Failure(errorMessage));
  }
};
